mapper.select:
select u.user_id, u.dept_id, u.factory_id, u.nick_name, u.user_name, u.email, u.avatar, u.phonenumber, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, d.dept_name, d.leader, f.factory_id, f.factory_name from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_factory f on u.factory_id = f.factory_id where u.del_flag = '0'
AND u.user_id = #{userId}
AND f.factory_id = #{factoryId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.nick_name like concat('%', #{nickName}, '%')
AND u.status = #{status}
AND u.phonenumber like concat('%', #{phonenumber}, '%')
AND date_format(u.create_time,'%Y%m%d') >= date_format(#{params.beginTime},'%Y%m%d')
AND date_format(u.create_time,'%Y%m%d') <= date_format(#{params.endTime},'%Y%m%d')
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))
${params.dataScope}

select distinct u.user_id, u.dept_id, u.factory_id, u.user_name, u.nick_name, u.email, u.phonenumber, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}

mapper.update:
update sys_user
dept_id = #{deptId}, factory_id = #{factoryId}, user_name = #{userName}, nick_name = #{nickName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, openid = #{openid}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where user_id = #{userId}

update sys_user
set status = #{status}
where user_id = #{userId}

update sys_user
set avatar = #{avatar}
where user_name = #{userName}

update sys_user
set password = #{password}
where user_name = #{userName}

mapper.delete:
update sys_user
set del_flag = '2'
where user_id = #{userId}
